document.addEventListener("DOMContentLoaded",()=>{const header=document.getElementById("main-header");if(!header)return;window.addEventListener("scroll",()=>{window.scrollY>10?header.classList.add("scrolled"):header.classList.remove("scrolled")});const toggleButton=document.getElementById("hamburger-toggle"),mobileMenu=document.getElementById("mobile-menu");if(!toggleButton||!mobileMenu)return;toggleButton.addEventListener("click",()=>{mobileMenu.classList.toggle("opened"),toggleButton.classList.toggle("open")});const dropdownToggles=document.querySelectorAll(".dropdown-toggle"),lgBreakpoint=1200;dropdownToggles.forEach(toggle=>{toggle.addEventListener("click",e=>{if(window.innerWidth<1200){e.preventDefault();const parentLi=toggle.closest("li.group");document.querySelectorAll(".header-menu li.group.is-open").forEach(openLi=>{openLi!==parentLi&&openLi.classList.remove("is-open")}),parentLi.classList.toggle("is-open")}})}),window.addEventListener("click",e=>{window.innerWidth<1200&&(e.target.closest(".header-menu li.group")||document.querySelectorAll(".header-menu li.group.is-open").forEach(openLi=>{openLi.classList.remove("is-open")}))}),window.addEventListener("resize",()=>{window.innerWidth>=1200&&document.querySelectorAll(".header-menu li.group.is-open").forEach(openLi=>{openLi.classList.remove("is-open")})})});